// Set the date we're counting down to
var countDownDate = new Date("July 30, 2017 23:59:59").getTime();

var x = setInterval(function () {

    var now = new Date().getTime();

    var distance = countDownDate - now;

    var days1 = Math.floor((distance / (1000 * 60 * 60 * 24))/10);
    var days2 = Math.floor((distance / (1000 * 60 * 60 * 24))%10);
    var hours1 = Math.floor(((distance % (1000 * 60 * 60 * 24)) / (1000 * 60 * 60))/10);
    var hours2 = Math.floor(((distance % (1000 * 60 * 60 * 24)) / (1000 * 60 * 60))%10);
    var minutes1 = Math.floor(((distance % (1000 * 60 * 60)) / (1000 * 60))/10);
    var minutes2 = Math.floor(((distance % (1000 * 60 * 60)) / (1000 * 60))%10);
    var seconds1 = Math.floor(((distance % (1000 * 60)) / 1000)/10);
    var seconds2 = Math.floor(((distance % (1000 * 60)) / 1000)%10);

    document.getElementById("days1").innerHTML = days1;
    document.getElementById("days2").innerHTML = days2;
    document.getElementById("hours1").innerHTML = hours1;
    document.getElementById("hours2").innerHTML = hours2;
    document.getElementById("minutes1").innerHTML = minutes1;
    document.getElementById("minutes2").innerHTML = minutes2;
    document.getElementById("seconds1").innerHTML = seconds1;
    document.getElementById("seconds2").innerHTML = seconds2;

    // If the count down is over, write some text
    if (distance < 0) {
        clearInterval(x);
        document.getElementById("hours").innerHTML = 0;
        document.getElementById("minutes").innerHTML = 0;
        document.getElementById("seconds").innerHTML = 0;
    }
}, 1000);